import React, { useState } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, List, ListItem, ListItemText, CircularProgress } from '@mui/material';

const Search: React.FC = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState<any[]>([]);
    const [loading, setLoading] = useState(false);

    const handleSearch = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('spotifyAccessToken');
            const response = await axios.get('https://api.spotify.com/v1/search', {
                headers: { Authorization: `Bearer ${token}` },
                params: {
                    q: query,
                    type: 'track,artist,album',
                },
            });
            setResults(response.data.tracks.items);
        } catch (error) {
            console.error('Error fetching search results:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                Search for a Song, Artist, or Album
            </Typography>
            <TextField
                label="Search"
                variant="outlined"
                fullWidth
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                margin="normal"
            />
            <Button variant="contained" color="primary" onClick={handleSearch} disabled={loading}>
                Search
            </Button>
            {loading ? (
                <CircularProgress />
            ) : (
                <List>
                    {results.length > 0 ? (
                        results.map((result) => (
                            <ListItem key={result.id}>
                                <ListItemText primary={result.name} secondary={result.artists.map((artist: any) => artist.name).join(', ')} />
                            </ListItem>
                        ))
                    ) : (
                        <Typography variant="body1">No results found</Typography>
                    )}
                </List>
            )}
        </Container>
    );
};

export default Search;
