import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Device } from '../types/device';
import querystring from "querystring";

const StartSession: React.FC = () => {
    const [devices, setDevices] = useState<Device[]>([]);
    const navigate = useNavigate();
    const client_id = 'e5bfb025a6744f459efaad327a7b9680';
    const redirect_uri = 'http://localhost:3000/callback';

    useEffect(() => {
        const fetchDevices = async () => {
            try {
                const token = localStorage.getItem('spotifyAccessToken'); // Securely store token.
                const response = await axios.get<{ devices: Device[] }>(
                    'https://api.spotify.com/v1/me/player/devices',
                    {
                        headers: { Authorization: `Bearer ${token}` },
                    }
                );
                setDevices(response.data.devices);
            } catch (error) {
                console.error('Error fetching devices:', error);
            }
        };

        fetchDevices();
    }, []);

    const generateRandomString = (length: number) => {
        let text = '';
        const possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        for (let i = 0; i < length; i++) {
            text += possible.charAt(Math.floor(Math.random() * possible.length));
        }
        return text;
    };

    const login = () => {
        const state = generateRandomString(16);
        const scope = 'user-read-private user-read-email';
        window.location.href = 'https://accounts.spotify.com/authorize?' +
            querystring.stringify({
                response_type: 'code',
                client_id: client_id,
                scope: scope,
                redirect_uri: redirect_uri,
                state: state
            });
    };

    const handleDeviceSelect = (deviceId: string) => {
        localStorage.setItem('selectedDevice', deviceId);
        navigate('/jukebox');
    };

    return (
        <div>
            <h1>Authenticate with Spotify</h1>
            <button onClick={login}>Login with Spotify</button>
            <h1>Choose a device to start the session</h1>
            {devices.map((device) => (
                <div
                    key={device.id}
                    onClick={() => handleDeviceSelect(device.id)}
                    style={{ cursor: 'pointer' }}
                >
                    {device.name}
                </div>
            ))}
        </div>
    );
};

export default StartSession;
