import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Typography, List, ListItem, ListItemText, Button, CircularProgress } from '@mui/material';

const Queue: React.FC = () => {
    const [queue, setQueue] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchQueue = async () => {
            try {
                const token = localStorage.getItem('spotifyAccessToken');
                const response = await axios.get('https://api.spotify.com/v1/me/player/queue', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setQueue(response.data.queue);
            } catch (error) {
                console.error('Error fetching queue:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchQueue();
    }, []);

    const removeFromQueue = async (uri: string) => {
        try {
            const token = localStorage.getItem('spotifyAccessToken');
            await axios.delete(`https://api.spotify.com/v1/me/player/queue`, {
                headers: { Authorization: `Bearer ${token}` },
                data: { uri },
            });
            setQueue(queue.filter(item => item.uri !== uri));
        } catch (error) {
            console.error('Error removing from queue:', error);
        }
    };

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                Spotify Queue
            </Typography>
            {loading ? (
                <CircularProgress />
            ) : (
                <List>
                    {queue.length > 0 ? (
                        queue.map((item) => (
                            <ListItem key={item.id} secondaryAction={
                                <Button variant="contained" color="secondary" onClick={() => removeFromQueue(item.uri)}>
                                    Remove
                                </Button>
                            }>
                                <ListItemText primary={item.name} secondary={item.artists.map((artist: any) => artist.name).join(', ')} />
                            </ListItem>
                        ))
                    ) : (
                        <Typography variant="body1">No items in queue</Typography>
                    )}
                </List>
            )}
        </Container>
    );
};

export default Queue;
