import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Device } from '../types/device';

const StartSession: React.FC = () => {
    const [devices, setDevices] = useState<Device[]>([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchDevices = async () => {
            try {
                const token = localStorage.getItem('spotifyAccessToken'); // Securely store token.
                const response = await axios.get<{ devices: Device[] }>(
                    'https://api.spotify.com/v1/me/player/devices',
                    {
                        headers: { Authorization: `Bearer ${token}` },
                    }
                );
                setDevices(response.data.devices);
            } catch (error) {
                console.error('Error fetching devices:', error);
            }
        };

        fetchDevices();
    }, []);

    const handleDeviceSelect = (deviceId: string) => {
        localStorage.setItem('selectedDevice', deviceId);
        navigate('/jukebox');
    };

    return (
        <div>
            <h1>Select a Device</h1>
            {devices.map((device) => (
                <div
                    key={device.id}
                    onClick={() => handleDeviceSelect(device.id)}
                    style={{ cursor: 'pointer' }}
                >
                    {device.name}
                </div>
            ))}
        </div>
    );
};

export default StartSession;
